import "../expenses_css/NewExpense.css";
import ExpenseForm from "./ExpenseForm";
import { v4 as uuidv4 } from "uuid";
import { useState } from "react/cjs/react.production.min";

const [isClicked, setClickStatus] = useState(false);

const NewExpense = (props) => {
  const { onAddData } = props;

  const saveFormData = (data) => {
    const newExpense = {
      ...data,
      id: uuidv4(),
    };

    console.log(newExpense);
    onAddData(newExpense);
  };

  let isClicked = false;

  const changeClickStatus = () => {
    setClickStatus(true);
  };

  return (
    <div className="new-expense">
      <button onClick={changeClickStatus} type="submit">
        Add New Expense
      </button>
      {isClicked && <ExpenseForm onDataSave={saveFormData} />}
    </div>
  );
};
export default NewExpense;
