import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";
import { useState } from "react";

const Expenses = (props) => {
  const { items } = props;
  const initialYear = new Date("July 20, 69 00:20:18");

  const [filteredYear, setFilteredYear] = useState(
    initialYear.getFullYear().toString()
  );

  const yearChangeHandler = (currentYear) => {
    setFilteredYear(currentYear);
  };

  const filteredExpense = items.filter((expense) => {
    return expense.date.getFullYear().toString() === filteredYear;
  });

  const renderExpenseItem = (item) => {
    return (
      <ExpenseItem
        title={item.title}
        amount={item.amount}
        date={item.date}
        key={item.id}
      />
    );
  };

  const expensesContent = <p>There is no expense in the given year</p>;

  if (filteredExpense.length() > 0) {
    expensesContent = filteredExpense.map(renderExpenseItem);
  }

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          onYearChange={yearChangeHandler}
          selectedYear={filteredYear}
        />
        {expensesContent}
      </Card>
    </div>
  );
};

export default Expenses;
